let successNoty=function(e){new Noty({theme:"relax",text:e,type:"success",layout:"topRight",timeout:1500}).show()},errorNoty=function(e){new Noty({theme:"relax",text:e,type:"error",layout:"topRight",timeout:1500}).show()};function toggleFriend(){let e=$(".add-removeFriend");e.click((function(r){r.preventDefault(),$.ajax({url:e.attr("href"),method:"get",success:function(r){"Friend Added"==r.message?(e.remove(),$("#friend-links").prepend(`<p>\n                    <a class="add-removeFriend" href="/friends/remove_friend/?friendId=${r.data.profile_user}">Remove Friend</a>\n                </p>`),successNoty("Friend Added Successfully")):(e.remove(),$("#friend-links").prepend(`<p>\n                    <a class="add-removeFriend" href="/friends/add_friend/?friendId=${r.data.profile_user}">Add Friend</a>\n                </p>`),successNoty("Friend Removed Successfully")),toggleFriend()}})}))}toggleFriend();